script:
frappe.ui.form.on('Portal Settings', {
	refresh: function(frm) {
	    if (cur_frm.doc.hide_standard_menu == '0') {
	      cur_frm.set_value('hide_standard_menu', '1');
	      cur_frm.save();
	    }
	    cur_frm.set_df_property('standard_menu_items','hidden','1');
	}
});

frappe.ui.form.on("Customer", {
	before_save: function(frm) {
		if (cur_frm.doc.customer_type == "Company") {
			cur_frm.set_df_property('customer_name','reqd','1');
			cur_frm.set_df_property('first_name','reqd','0');
			cur_frm.set_df_property('last_name','reqd','0');
			cur_frm.set_value('first_name', cur_frm.doc.customer_name);
			cur_frm.set_value('last_name', cur_frm.doc.customer_name);
		}
		if (cur_frm.doc.customer_type == "Individual") {
			var fullname = cur_frm.doc.first_name + ' ' + cur_frm.doc.last_name;
			cur_frm.set_value('customer_name', fullname);
		}
	}
});
